mapper.select:
SELECT *,IF(apm.menu_id is not NULL, '1' , '0') checks FROM auth_menu am left JOIN auth_post_menu apm ON apm.menu_id = am.id AND apm.post_id = #{postId}
WHERE am.p_id
#{id}
GROUP BY am.ID order BY am.id

SELECT * FROM auth_menu am inner JOIN auth_post_menu apm ON apm.menu_id = am.id AND apm.post_id = #{postId}  AND am.is_menu =#{isMenu}

     AND am.p_id = #{menuId}

ORDER  BY  am.id

select * from auth_menu am 

   am.id in(select auth_id from user_menu where user_menu.user_id=#{userId})
   AND am.is_menu =#{isMenu} 

        AND am.p_id = #{menuId}

        ORDER  BY  am.id
